import requests 
import csv
csvfile = 'C:/Users/vineeth.vijay.das/Desktop/Python/Web_Scrapping/example.csv'

import bs4
a = 'https://timesofindia.indiatimes.com/2010/1/1/archivelist/year-2010,month-1,starttime-40179.cms' 
res = requests.get(a)
soup = bs4.BeautifulSoup(res.text,'lxml')
#print(soup.text)

b = soup.find_all("span",{"style":"font-family:arial ;font-size:12;color: #006699"})
#print(b)
with open(csvfile,"w") as output:
    writer = csv.writer(output,lineterminator='\n')
    for i in b:
        for j in i:
            #print(j)
            writer.writerow([j.encode("utf-8")])
        
        


